installation.script:
#!/bin/bash -e
cd /mnt/server
echo "https://apk.bell-sw.com/main" | tee -a /etc/apk/repositories
wget -P /etc/apk/keys/ https://apk.bell-sw.com/info@bell-sw.com-5fea454e.rsa.pub
apk add bellsoft-java11
apk add git
apk add curl
apk --no-cache add ca-certificates wget
wget -q -O /etc/apk/keys/sgerrand.rsa.pub https://alpine-pkgs.sgerrand.com/sgerrand.rsa.pub
wget https://github.com/sgerrand/alpine-pkg-glibc/releases/download/2.28-r0/glibc-2.28-r0.apk
apk add glibc-2.28-r0.apk
rm glibc-2.28-r0.apk
COMMIT_RUNTIME=master
COMMIT_LAUNCHER=master
#COMMIT_MODULES=master
# Clone repo
echo -e "\033[32mPhase 1: \033[33mClone main repository\033[m";
git clone -b $COMMIT_LAUNCHER https://github.com/GravitLauncher/Launcher.git src;
cd src;
sed -i 's/git@github.com:/https:\/\/github.com\//' .gitmodules;
git submodule sync;
git submodule update --init --recursive;
if [[ -n $COMMIT_MODULES ]]; then
cd modules;
git checkout $COMMIT_MODULES;
cd ..;
fi;
echo -e "\033[32mPhase 2: \033[33mBuild\033[m";
./gradlew -Dorg.gradle.daemon=false build || ( echo -e "\033[31mBuild failed. Stopping\033[m" && exit 101 );
cd ..;
# Clone runtime
echo -e "\033[32mPhase 3: \033[33mClone runtime repository\033[m";
git clone -b $COMMIT_RUNTIME https://github.com/GravitLauncher/LauncherRuntime.git srcRuntime;
cd srcRuntime;
./gradlew -Dorg.gradle.daemon=false build || ( echo -e "\033[31mBuild failed. Stopping\033[m" && exit 102 );
cd ..;
# Links
echo -e "\033[32mPhase 4: \033[33mLinks\033[m";
## LaunchServer
cp /mnt/server/src/LaunchServer/build/libs/LaunchServer.jar /mnt/server/;
cp -r /mnt/server/src/LaunchServer/build/libs/libraries .;
cp -r /mnt/server/src/LaunchServer/build/libs/launcher-libraries /mnt/server/;
cp -r /mnt/server/src/LaunchServer/build/libs/launcher-libraries-compile /mnt/server/;
chmod -R +x libraries/launch4j;
## Modules
mkdir modules;
cp srcRuntime/runtime .;
mkdir launcher-modules;
cp ../$(echo srcRuntime/build/libs/JavaRuntime-*.jar) launcher-modules/;
## Compat
mkdir compat;
cp -r /mnt/server/src/compat /mnt/server/compat/core;
cp -r /mnt/server/srcRuntime/compat /mnt/server/compat/runtime;
cp -r /mnt/server/src/ServerWrapper/build/libs/ServerWrapper.jar /mnt/server/compat/;
cp -r /mnt/server/src/LauncherAuthlib/build/libs/LauncherAuthlib.jar /mnt/server/compat/;
# Finished small help and simpliest check of launch4j
echo -e "\033[31mNOT DELETE DIRECTORIES src AND srcRuntime\033[m";
